/**
 * Prepare admin pages header
 *
 * @return array
 */
function googleapiAdminPrepareHead()
{
	global $langs, $conf;

	$langs->loadLangs(['oauth', 'googleapi@googleapi']);

	$h = 0;
	$head = [];

	$head[$h][0] = dol_buildpath("/googleapi/admin/setup.php", 1);
	$head[$h][1] = $langs->trans("GoogleApiSettings");
	$head[$h][2] = 'settings';
	$h++;

	$head[$h][0] = dol_buildpath('/googleapi/admin/oauthlogintokens.php', 1);
	$head[$h][1] = $langs->trans("GoogleApiTokenManager");
	$head[$h][2] = 'tokengeneration';
	$h++;

	$head[$h][0] = dol_buildpath("/googleapi/admin/about.php", 1);
	$head[$h][1] = $langs->trans("GoogleApiAbout");
	$head[$h][2] = 'about';
	$h++;

	// Show more tabs from modules
	complete_head_from_modules($conf, $langs, null, $head, $h, 'googleapi');

	return $head;
}

/**
 * Complete $object to change ->label and ->note before pushing event to Google Calendar.
 *
 * @param   Object      $object     Object event to complete
 * @param   Translate   $langs      Language object
 * @return  void
 */
function googleapi_complete_label_and_note(&$object, $langs)
{
	global $conf, $db, $langs;
	global $dolibarr_main_url_root;

	$eventlabel = trim($object->label);
	// Define $urlwithroot
	$urlwithouturlroot = preg_replace('/' . preg_quote(DOL_URL_ROOT, '/') . '$/i', '', trim($dolibarr_main_url_root));
	// This is to use external domain name found into config file
	$urlwithroot = $urlwithouturlroot . DOL_URL_ROOT;
	// This is to use same domain name than current
	//$urlwithroot=DOL_MAIN_URL_ROOT;
	if (($object->socid > 0 || (!empty($object->thirdparty->id) && $object->thirdparty->id > 0)) && empty($conf->global->GOOGLEAPI_DISABLE_EVENT_LABEL_INC_SOCIETE)) {
		$thirdparty = new Societe($db);
		$result = $thirdparty->fetch($object->socid ? $object->socid : $object->thirdparty->id);
		if ($result > 0) {
			// we add thirdparty name into label
			$eventlabel .= ' - ' . $thirdparty->name;
			$tmpadd = $thirdparty->getFullAddress(0);
			// we add other info into note
			$more = '';
			if ($tmpadd && empty($conf->global->GOOGLEAPI_DISABLE_ADD_ADDRESS_INTO_DESC)) {
				$more .= "<br>" . $thirdparty->name . "<br>" . $thirdparty->getFullAddress(1) . "<br>";
			}
			if (!empty($thirdparty->phone)) {
				$more .= "<br>" . $langs->trans("Phone") . ': ' . $thirdparty->phone;
			}
			if (!empty($thirdparty->phone_pro)) {
				$more .= "<br>" . $langs->trans("Phone") . ': ' . $thirdparty->phone_pro;
			}
			if (!empty($thirdparty->fax)) {
				$more .= "<br>" . $langs->trans("Fax") . ': ' . $thirdparty->fax;
			}

			$urltoelem = '<a href="' . $urlwithroot . '/societe/soc.php?socid=' . $thirdparty->id . '">' . $thirdparty->name . '</a>';
			$object->note .= "<br>" . $more . "<br>" . $langs->trans("GoogleApiLinkToThirdParty") . ': ' . $urltoelem;
		}
	}
	if (($object->contactid > 0 || (!empty($object->contact->id) && $object->contact->id > 0)) && empty($conf->global->GOOGLEAPI_DISABLE_EVENT_LABEL_INC_CONTACT)) {
		$contact = new Contact($db);
		$result = $contact->fetch($object->contactid ? $object->contactid : $object->contact->id);
		if ($result > 0) {
			// we add contact fullname into label
			$eventlabel .= ' - ' . $contact->getFullName($langs, 1);
			$tmpadd = $contact->getFullAddress(0);
			// we add other info into note
			$more = '';
			if ($tmpadd && empty($conf->global->GOOGLEAPI_DISABLE_ADD_ADDRESS_INTO_DESC)) {
				$more .= "<br>" . $contact->lastname . "<br>" . $contact->getFullAddress(1) . "<br>";
			}
			if (!empty($contact->phone)) {
				$more .= "<br>" . $langs->trans("Phone") . ': ' . $contact->phone;
			}
			if (!empty($contact->phone_pro)) {
				$more .= "<br>" . $langs->trans("Phone") . ': ' . $contact->phone_pro;
			}
			if (!empty($contact->phone_perso)) {
				$more .= "<br>" . $langs->trans("PhonePerso") . ': ' . $contact->phone_perso;
			}
			if (!empty($contact->phone_mobile)) {
				$more .= "<br>" . $langs->trans("PhoneMobile") . ': ' . $contact->phone_mobile;
			}
			if (!empty($contact->fax)) {
				$more .= "<br>" . $langs->trans("Fax") . ': ' . $contact->fax;
			}

			$urltoelem = '<a href="' . $urlwithroot . '/contact/card.ph?id=' . $contact->id . '">' . $contact->getFullName($langs, 1) . '</a>';
			$object->note .= "<br>" . $more . "<br>" . $langs->trans("GoogleApiLinkToContact") . ': ' . $urltoelem;
		}
	}
	$object->label = $eventlabel;
}

/**
 * Get GoogleApi Client
 * @param   User    $fuser  propriétaire du token
 * @return  Google_Client|bool
 */
function getGoogleApiClient($fuser)
{
	global $conf;

	$client = false;

	$provider = new Google([
		'clientId'     => getDolGlobalString('OAUTH_GOOGLEAPI_ID'),
		'clientSecret' => getDolGlobalString('OAUTH_GOOGLEAPI_SECRET'),
		'redirectUri' => dol_buildpath('/googleapi/core/modules/oauth/googleapi_oauthcallback.php', 2),
		//'hostedDomain' => 'example.com', // optional; used to restrict access to users on your G Suite/Google Apps for Business accounts
		'accessType' => 'offline',
	]);

	$token = retrieveAccessToken('GoogleApi', $fuser->id);
	// Is token expired or will token expire in the next 30 seconds
	if (is_object($token)) {
		$expire = time() > ($token->getExpires() - 30);
		if ($expire) {
			try {
				// il faut sauvegarder le refresh token car google ne le donne qu'une seule fois
				$refreshtoken = $token->getRefreshToken();
				$refreshtokenbackup = retrieveRefreshTokenBackup('GoogleApi', $fuser->id);
				if (empty($refreshtoken) && !empty($refreshtokenbackup)) {
					$refreshtoken = $refreshtokenbackup;
				}
				$grant = new RefreshToken();
				$token = $provider->getAccessToken($grant, ['refresh_token' => $refreshtoken]);
				//$token->setRefreshToken($refreshtoken);
				storeAccessToken('GoogleApi', $token, $refreshtoken, $fuser->id);
			} catch (Exception $e) {
				dol_syslog($e->getMessage(), LOG_WARNING);
			}
		}
		$client = new Google_Client();
		$client->setAccessToken($token->getToken());
	}

	return $client;
}

/**
 * Create agenda event from task
 *
 * @param   User    $owner          Owner of actioncomm
 * @param   string  $type_code      code actioncomm
 * @param   string  $date_start     start date of evt (gmt)
 * @param   string  $date_end       end date of evt (gmt)
 * @param   string  $label          label of event
 * @param   string  $description    description
 * @param   string  $location       location of evt (128 chars)
 * @param   string  $googleapiId        Id of graph evt
 * @param   int     $fulldayevent   event is full day or not
 * @param   int     $notrigger      0 or 1 for no trigger
 * @return  int                     <0 not ok, >0 ok
 */
function googleapiCreateActioncomm($owner, $type_code, $date_start, $date_end, $label, $description = '', $location = '', $googleapiId = '', $fulldayevent = 0, $notrigger = 0)
{
	global $db, $conf, $langs;

	$langs->load('googleapi@googleapi');

	dol_syslog("googleapiCreateActioncomm " . $date_start . " => " . $date_end, LOG_NOTICE);

	include_once DOL_DOCUMENT_ROOT . '/comm/action/class/actioncomm.class.php';
	$actioncomm = new ActionComm($db);

	$actioncomm->type_code = $type_code;
	$actioncomm->userownerid = $owner->id;
	//$actioncomm->authorid = $owner->id;
	$transparency = 1;
	// $actioncomm->userassigned[$object->fk_user] = [
	//     'id' => $object->fk_user,
	//     'transparency' => $transparency,
	// ];
	$actioncomm->transparency = $transparency;
	$actioncomm->priority = 0;
	$actioncomm->fulldayevent = $fulldayevent;
	$actioncomm->label = empty($label) ? 'NoSubject' : $label;
	$actioncomm->location = $location;
	//$actioncomm->fk_element = $object->id;
	//$actioncomm->elementtype = $object->element;
	$actioncomm->fk_project = null;
	$actioncomm->datep = $date_start;
	$actioncomm->datef = $date_end;
	$actioncomm->percentage = -1;
	// il y a un dol_htmlcleanlastbr
	// Replace bad character by '?' including utf8 four bytes
	// $description = preg_replace('/[x00-x08x10x0Bx0Cx0E-x19x7F]|[x00-x7F][x80-xBF]+|([xC0xC1]|[xF0-xFF])[x80-xBF]*'.
	// '|[xC2-xDF]((?![x80-xBF])|[x80-xBF]{2,})|[xE0-xEF](([x80-xBF](?![x80-xBF]))|(?![x80-xBF]{2})|[x80-xBF]{3,})/S', '?', $description);
	// $description = preg_replace('/xE0[x80-x9F][x80-xBF]|xED[xA0-xBF][x80-xBF]/S', '?', $description);
	$actioncomm->note = $description;
	$actioncomm->note_private = $description;
	$actioncomm->contact = null;
	$actioncomm->socid = null;
	$actioncomm->societe = null;

	// context for triggers
	$actioncomm->context['googleapi'] = $googleapiId;
	// ActionComm create
	$resultid = $actioncomm->create($owner, $notrigger);
	if ($resultid > 0) {
		$actioncomm->array_options['options_googleapi_EventId'] = $googleapiId;
		$actioncomm->insertExtraFields();
		dol_syslog("googleapiCreateActioncomm SUCCESS: " . $resultid, LOG_NOTICE);
		// Add Linked object
		//$actioncomm->id = $idaction;
		//$actioncomm->add_object_linked($object->element, $object->id);
	} else {
		dol_syslog("googleapiCreateActioncomm error: " . $actioncomm->errorsToString(), LOG_ERR);
		//setEventMessages($actioncomm->error, $actioncomm->errors, 'errors');
	}
	return $resultid;
}

