	// $sql .= " AND fk_source = ".$object->id;
	// $sql .= " AND targettype = '".$db->escape($actioncomm->element)."'";
	$sql = 'SELECT id FROM ' . MAIN_DB_PREFIX . 'actioncomm WHERE fk_element=' . $object->id . ' AND elementtype="' . $db->escape($object->element) . '"';
	//var_dump($sql);exit;
	$resql = $db->query($sql);

	while ($resql && $obj = $db->fetch_object($resql)) {
		//var_dump($obj);exit;
		setEventMessage('actioncomm delete' . $obj->id);
		$actioncomm = new ActionComm($db);
		$result = $actioncomm->fetch($obj->id);
		$actioncomm->fetch_optionals();
		$actioncomm->fetch_userassigned();
		$actioncomm->context['googleapi'] = $object->array_options['options_googleapi_EventId'];
		$actioncomm->oldcopy = clone $actioncomm;
		if ($result > 0 /*&& $actioncomm->label == $holiday_label*/) {
			// On supprime l'agenda
			$ret = $actioncomm->delete();
			if ($ret > 0) {
				// Linked object
				// $ret = $actioncomm->deleteObjectLinked(null, null, null, null, $rowid);
				// if ($ret < 0) {
				//     dol_syslog("googleapiDeleteActioncomm Error delete link: " . $actioncomm->errorsToString(), LOG_ERR);
				//     setEventMessages($actioncomm->error, $actioncomm->errors, 'errors');
				//     return -1;
				// }
			} else {
				dol_syslog("googleapiDeleteActioncomm Error delete: " . $actioncomm->errorsToString(), LOG_ERR);
				setEventMessages($actioncomm->error, $actioncomm->errors, 'errors');
				return -1;
			}
		}
	}
	return 1;
}

/**
 * FindBadUtf8
 *
 * @param   string $string  String
 * @return  boolean
 */
function googleapiFindBadUtf8($string)
{
	$regex = '([\x00-\x7F]';
	$regex .= '|[\xC2-\xDF][\x80-\xBF]';
	$regex .= '|\xE0[\xA0-\xBF][\x80-\xBF]';
	$regex .= '|[\xE1-\xEC\xEE\xEF][\x80-\xBF]{2}';
	$regex .= '|\xED[\x80-\x9F][\x80-\xBF]';
	$regex .= '|\xF0[\x90-\xBF][\x80-\xBF]{2}';
	$regex .= '|[\xF1-\xF3][\x80-\xBF]{3}';
	$regex .= '|\xF4[\x80-\x8F][\x80-\xBF]{2}';
	$regex .= '|(.{1}))';

	while (preg_match('/' . $regex . '/S', $string, $matches)) {
		if (isset($matches[2])) {
			return true;
		}
		$string = substr($string, strlen($matches[0]));
	}

	return false;
}

